  extends Omit<FormHTMLAttributes<HTMLFormElement>, "onSubmit"> {}

interface LoginFormProps extends FormProps {
  onSubmit: (data: z.infer<typeof loginFormSchema>) => void;
  defaultValues?: z.infer<typeof loginFormSchema>;
}

