"use client";

import { Input } from "@/components/ui/input";
import { cn } from "@/lib/utils";

import { Eye, EyeOff } from "lucide-react";
import type { ComponentProps, HTMLAttributes } from "react";
import { useState } from "react";

export default function PasswordInput({
  className,
  containerProps,
  ...props
}: ComponentProps<typeof Input> & {
  containerProps?: HTMLAttributes<HTMLDivElement>;
}) {
  const [isVisible, setIsVisible] = useState<boolean>(false);

  const toggleVisibility = () => setIsVisible((prevState) => !prevState);

  return (
    <div
      {...containerProps}
      className={cn("relative", containerProps?.className)}
    >
      <Input
        className={cn("pe-9", className)}
        placeholder="Password"
        type={isVisible ? "text" : "password"}
        {...props}
      />
      <button
        type="button"
        className="absolute inset-y-0 end-0 flex h-full w-9 items-center justify-center rounded-e-lg text-muted-foreground/80 outline-offset-2 transition-colors hover:text-foreground focus:z-10 focus-visible:outline-2 focus-visible:outline-ring/70 disabled:pointer-events-none disabled:cursor-not-allowed disabled:opacity-50"
        onClick={toggleVisibility}
        aria-label={isVisible ? "Hide password" : "Show password"}
        aria-pressed={isVisible}
        aria-controls="password"
      >
        {isVisible ? (
          <EyeOff size={16} strokeWidth={2} aria-hidden="true" />
        ) : (
          <Eye size={16} strokeWidth={2} aria-hidden="true" />
        )}
      </button>
    </div>
  );
}

import { Input } from "@/components/ui/input";
import { AsYouType, type CountryCode } from "libphonenumber-js";
import type { ComponentProps } from "react";
import { NumberFormatBase } from "react-number-format";

interface PhoneInputFieldProps
  extends Omit<
    ComponentProps<typeof Input>,
    "type" | "defaultValue" | "onChange"
  > {
  value?: string;
  defaultValue?: string | number | null | undefined;
  onChange?: (value: string) => void;
}

function phoneFormatter(countryCode: CountryCode, value: string) {
  const asYouType = new AsYouType(countryCode);
  return asYouType.input(value);
}

export default function PhoneInputField({
  value,
  onChange,
  defaultValue,
  ...props
}: PhoneInputFieldProps) {
  return (
    <NumberFormatBase
      type="tel"
      value={value}
      customInput={Input}
      placeholder="Phone number"
      getInputRef={props.ref}
      className={props.className}
      defaultValue={defaultValue}
      format={(value) => phoneFormatter("US", value.slice(0, 15))}
      onValueChange={(e) => {
        onChange?.(e.value);
      }}
      {...props}
    />
  );
}
